mapper.select:
SELECT * 
  FROM t_user_info


		AND id = #{id}


		AND userId = #{userId}


		AND realName = #{realName}


		AND idCardNo = #{idCardNo}


		AND sex = #{sex}


		AND contactAddress = #{contactAddress}


		AND mobile = #{mobile}


		AND eMail = #{eMail}


		AND weixiNumber = #{weixiNumber}


		AND qqNumber = #{qqNumber}


		AND zfbNumber = #{zfbNumber}


		AND cftNumber = #{cftNumber}


		AND bankName = #{bankName}


		AND province = #{province}


		AND city = #{city}


		AND bankSubbranch = #{bankSubbranch}


		AND bankAccount = #{bankAccount}


		AND openAccount = #{openAccount}

mapper.update:
UPDATE t_user_info 
   SET 		

		realName = #{realName}


		,idCardNo = #{idCardNo}


		,sex = #{sex}


		,contactAddress = #{contactAddress}


		,mobile = #{mobile}


		,eMail = #{eMail}


		,weixiNumber = #{weixiNumber}


		,qqNumber = #{qqNumber}


		,zfbNumber = #{zfbNumber}


		,cftNumber = #{cftNumber}


		,bankName = #{bankName}


		,province = #{province}


		,city = #{city}


		,bankSubbranch = #{bankSubbranch}


		,bankAccount = #{bankAccount}


		,openAccount = #{openAccount}

WHERE userId = #{userId}
